insert  into `dbdocsfields`(`tableName`,`fieldName`,`fieldNotes`) values ('creature_addon','bytes1','The value here overrides the value for the creature\'s unit field UNIT_FIELD_BYTES_1. \r\n\r\nList of known values and what their visual effects on the creature \r\n\r\n<table border=\'1\' cellspacing=\'1\' cellpadding=\'3\' bgcolor=\'#f0f0f0\'>\r\n<tr bgcolor=\'#f0f0ff\'>\r\n<th><b>Value</b></th>\r\n<th align=\'left\'><b>Meaning</b></th>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>1</td><td align=\'left\' valign=\'middle\'>Sitting on Ground</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>\r\n2</td><td align=\'left\' valign=\'middle\'>Creature that can fly and are not on the ground appear to have this flag. If they are on the ground, flag is not present.</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>\r\n3</td><td align=\'left\' valign=\'middle\'>Sleep</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>\r\n4</td><td align=\'left\' valign=\'middle\'>Untrackable</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>\r\n7</td><td align=\'left\' valign=\'middle\'>Makes the mob lying dead (combined with empty health bar (dynamicflags = 32) and gray name (dynamicflags = 4) makes the mob to appear dead)</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>\r\n8</td><td align=\'left\' valign=\'middle\'>Makes the mob kneel (need bytes2 = 1)</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>\r\n9</td><td align=\'left\' valign=\'middle\'>Submerges the creature below the ground</td></tr>\r\n</table>\r\n');
insert  into `dbdocsfields`(`tableName`,`fieldName`,`fieldNotes`) values ('creature_addon','emote','Emote ID that the creature should continually perform.\r\n\r\nList of often used emote IDs and what they do ie. 65 Makes the creature look dead by lying on the ground.\r\n\r\n<table border=\'1\' cellspacing=\'1\' cellpadding=\'3\' bgcolor=\'#f0f0f0\'>\r\n<tr bgcolor=\'#f0f0ff\'>\r\n<th><b>Value</b></th>\r\n<th align=\'left\'><b>Emote Name</b></th>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>10</td><td align=\'left\' valign=\'middle\'>EMOTE_STATE_DANCE</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>\r\n12</td><td align=\'left\' valign=\'middle\'>EMOTE_STATE_SLEEP</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>\r\n13</td><td align=\'left\' valign=\'middle\'>EMOTE_STATE_SIT</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>\r\n26</td><td align=\'left\' valign=\'middle\'>EMOTE_STATE_STAND</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>\r\n28</td><td align=\'left\' valign=\'middle\'>EMOTE_STATE_WORK</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>\r\n64</td><td align=\'left\' valign=\'middle\'>EMOTE_STATE_STUN</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>\r\n64</td><td align=\'left\' valign=\'middle\'>EMOTE_STATE_DEAD</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>\r\n68</td><td align=\'left\' valign=\'middle\'>EMOTE_STATE_KNEEL</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>\r\n70</td><td align=\'left\' valign=\'middle\'>EMOTE_ONESHOT_WAVE_NOSHEATHE</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>\r\n71</td><td align=\'left\' valign=\'middle\'>EMOTE_ONESHOT_CHEER_NOSHEATHE</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>\r\n92</td><td align=\'left\' valign=\'middle\'>EM