insert  into `dbdocsfields`(`tableName`,`fieldName`,`fieldNotes`) values ('creature_linking_template','flag','flag - describing what should happen when\r\n\r\n<table border=\"1\" cellpadding=\"3\" cellspacing=\"0\">\r\n<caption>Possible values</caption>\r\n<tr><th>Value</th><th>Flag Name</th><th>Comment</th></tr>\r\n<tr><td align=\"center\" valign=\"middle\">1</td><td align=\"center\" valign=\"middle\">FLAG&#95;AGGRO&#95;ON&#95;AGGRO</td><td align=\"left\" valign=\"middle\">The &quot;slave&quot; aggroes when the &quot;master&quot; aggroes</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">2</td><td align=\"center\" valign=\"middle\">FLAG&#95;TO&#95;AGGRO&#95;ON&#95;AGGRO</td><td align=\"left\" valign=\"middle\">The master aggroes when the slave aggroes</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">4</td><td align=\"center\" valign=\"middle\">FLAG&#95;RESPAWN&#95;ON&#95;EVADE</td><td align=\"left\" valign=\"middle\">The slave respawns when the master evades</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">8</td><td align=\"center\" valign=\"middle\">FLAG&#95;TO&#95;RESPAWN&#95;ON&#95;EVADE</td><td align=\"left\" valign=\"middle\">the master respawns when the slave evades</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">16</td><td align=\"center\" valign=\"middle\">FLAG&#95;DESPAWN&#95;ON&#95;DEATH</td><td align=\"left\" valign=\"middle\">the slave despawns when the master dies</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">32</td><td align=\"center\" valign=\"middle\">FLAG&#95;SELFKILL&#95;ON&#95;DEATH</td><td align=\"left\" valign=\"middle\">the slave goes suicide when the master dies</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">64</td><td align=\"center\" valign=\"middle\">FLAG&#95;RESPAWN&#95;ON&#95;DEATH</td><td align=\"left\" valign=\"middle\">the slave respawn when the master dies</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">128</td><td align=\"center\" valign=\"middle\">FLAG&#95;RESPAWN&#95;ON&#95;RESPAWN</td><td align=\"left\" valign=\"middle\">the slave respawns on master respawn</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">256</td><td align=\"center\" valign=\"middle\">FLAG&#95;DESPAWN&#95;ON&#95;RESPAWN</td><td align=\"left\" valign=\"middle\">the slave despawns on master respawn (TODO: check for slave != master)</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">512</td><td align=\"center\" valign=\"middle\">FLAG&#95;FOLLOW</td><td align=\"left\" valign=\"middle\">the slave follows the master, very basic, see TODO notes in commit, or post below</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">1024</td><td align=\"center\" valign=\"middle\">FLAG&#95;CANT&#95;SPAWN&#95;IF&#95;BOSS&#95;DEAD</td><td align=\"left\" valign=\"middle\">the slave cannot respawn while boss is dead</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">2048</td><td align=\"center\" valign=\"middle\">FLAG&#95;CANT&#95;SPAWN&#95;IF&#95;BOSS&#95;ALIVE</td><td align=\"left\" valign=\"middle\">the slave cannot respawn while boss is alive</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">4096</td><td align=\"center\" valign=\"middle\">FLAG&#95;DESPAWN&#95;ON&#95;EVADE</td><td align=\"left\" valign=\"middle\">the slave despawn after the master evade</td></tr>\r\n<tr><td align=\"center\" valign=\"middle\">8192</td><td align=\"center\" valign=\"middle\">FLAG&#95;DESPAWN&#95;ON&#95;DESPAWN</td><td align=\"left\" valign=\"middle\">the slave despawn after the master despawns</td></tr>\r\n</table>\r\n');
